const Fecha = new Date().toISOString();
const tipo = "chat";
const origen = "webhook";

return {
  json: {
    sessionid: $json.sessionid,
    Pregunta: $json.Pregunta,
    Respuesta: $json.RespuestaFinal,
    Fecha,
    tipo,
    origen
  }
};

const texto = ( $input.first().json.respuestaTexto || '')
  .trim()
  .replace(/\[(https?:\/\/[^\]]+)\]\(\1\)/g, '$1');

const bloques = texto
  .split(/\n\s*\d+\.\s*\*\*(?=\S)/)
  .map((b, i) => `**${b.trim()}`)
  .filter(b =>
    //b.includes('Stock:') &&
    b.includes('https://')
  );

const productosProcesados = new Set();

return bloques.map((b, i) => {
  try {
    let producto = (b.match(/\*\*(.*?)\*\*/) || [])[1]?.trim();
    if (!producto) {
      producto = (b.match(/CES:\s*([^*]+)/i) || [])[1]?.trim() || 'Producto sin nombre';
    }

    let parte = (b.match(/Parte:\s*(.+?)(\n|$)/i) || [])[1]?.trim();

    const urlOriginal = (b.match(/https?:\/\/[^\s)]+/) || [])[0] || 'N/A';

    if (!parte) {
      const pMatch = urlOriginal?.match(/p=([^&\s]+)/i);
      parte = pMatch ? pMatch[1] : 'N/A';
    }

    let nuevaParteParaURL = parte
      .replace(/\+/g, '%sum')
      .replace(/\//g, '_slsh_');

    const url = `https://www.agelectronica.com/detalle?busca=${nuevaParteParaURL}`;

    const stock = (b.match(/Stock:\s*([0-9]+)/i) || [])[1] || 'N/A';
    const precio = (b.match(/Precio sin IVA:\s*\$?([0-9.,]+)/i) || [])[1] || 'N/A';
    const precioIVA = (b.match(/Precio con IVA:\s*\$?([0-9.,]+)/i) || [])[1] || 'N/A';

    const hashId = `${producto}|${parte}|${stock}|${url}`;
    if (productosProcesados.has(hashId)) return null;
    productosProcesados.add(hashId);

    return {
      json: {
        producto,
        parte,            
        stock,
        Precio: precio,
        PrecioIVA: precioIVA,
        //url,              
        posicion: i + 1
      }
    };
  } catch (error) {
    return { json: { error: error.message, bloque: b } };
  }
}).filter(Boolean);

const texto = $json.respuestaTexto || '';

let urls = texto.match(/https?:\/\/[a-zA-Z0-9.\-_:~/?#@!$&'()*+,;=%]+/g) || [];

const seen = new Set();
urls = urls
  .map(url => url.trim().replace(/\/+$/, ''))
  .filter(url => {
    if (seen.has(url)) return false;
    seen.add(url);
    return true;
  });

return urls.map(original => {
  const match = original.match(/p=([^&\s]+)/i);
  if (!match) {
    return { json: { urlProducto: original } };
  }

  let parte = match[1];
  parte = parte.replace(/\+/g, '%sum').replace(/\//g, '_slsh_');

  const urlProducto = `https://www.agelectronica.com/detalle?busca=${parte}`;

  return { json: { urlProducto } };
});

